import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { z } from "zod";
import { insertUserSchema, insertAppointmentSchema, insertMedicalRecordSchema, insertHealthMetricsSchema, insertContactMessageSchema, insertFeedbackSchema } from "@shared/schema";
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";

const JWT_SECRET = process.env.JWT_SECRET || "healthcare-secret-key";

// Middleware to verify JWT token
const authenticateToken = (req: any, res: any, next: any) => {
  const authHeader = req.headers['authorization'];
  const token = authHeader && authHeader.split(' ')[1];

  if (!token) {
    return res.status(401).json({ message: 'Access token required' });
  }

  jwt.verify(token, JWT_SECRET, (err: any, user: any) => {
    if (err) return res.status(403).json({ message: 'Invalid token' });
    req.user = user;
    next();
  });
};

// Middleware to check user role
const authorizeRole = (roles: string[]) => {
  return (req: any, res: any, next: any) => {
    if (!req.user || !roles.includes(req.user.role)) {
      return res.status(403).json({ message: 'Insufficient permissions' });
    }
    next();
  };
};

export async function registerRoutes(app: Express): Promise<Server> {

  // Authentication Routes
  app.post("/api/auth/register", async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(userData.email);
      if (existingUser) {
        return res.status(400).json({ message: "User already exists" });
      }

      // Hash password
      const hashedPassword = await bcrypt.hash(userData.password, 10);

      const user = await storage.createUser({
        ...userData,
        password: hashedPassword
      });

      // Remove password from response
      const { password, ...userResponse } = user;

      res.status(201).json({ user: userResponse });
    } catch (error) {
      res.status(400).json({ message: "Invalid user data", error });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValidPassword = await bcrypt.compare(password, user.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const token = jwt.sign(
        { userId: user.id, email: user.email, role: user.role },
        JWT_SECRET,
        { expiresIn: '24h' }
      );

      const { password: _, ...userResponse } = user;

      res.json({ token, user: userResponse });
    } catch (error) {
      res.status(500).json({ message: "Login failed", error });
    }
  });

  // User Routes
  app.get("/api/users/profile", authenticateToken, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userResponse } = user;
      res.json(userResponse);
    } catch (error) {
      res.status(500).json({ message: "Failed to get profile", error });
    }
  });

  app.put("/api/users/profile", authenticateToken, async (req: any, res) => {
    try {
      const updates = req.body;
      delete updates.password; // Don't allow password updates through this route

      const user = await storage.updateUser(req.user.userId, updates);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userResponse } = user;
      res.json(userResponse);
    } catch (error) {
      res.status(500).json({ message: "Failed to update profile", error });
    }
  });

  app.get("/api/users", authenticateToken, authorizeRole(['admin']), async (req, res) => {
    try {
      const { role } = req.query;
      let users;

      if (role) {
        users = await storage.getUsersByRole(role as string);
      } else {
        users = await storage.getAllUsers();
      }

      // Remove passwords from response
      const usersResponse = users.map(({ password, ...user }) => user);
      res.json(usersResponse);
    } catch (error) {
      res.status(500).json({ message: "Failed to get users", error });
    }
  });

  // Appointment Routes
  app.get("/api/appointments", authenticateToken, async (req: any, res) => {
    try {
      let appointments;

      if (req.user.role === 'patient') {
        appointments = await storage.getAppointmentsByPatient(req.user.userId);
      } else if (req.user.role === 'doctor') {
        appointments = await storage.getAppointmentsByDoctor(req.user.userId);
      } else if (req.user.role === 'admin' || req.user.role === 'nurse') {
        appointments = await storage.getAllAppointments();
      } else {
        return res.status(403).json({ message: "Access denied" });
      }

      res.json(appointments);
    } catch (error) {
      res.status(500).json({ message: "Failed to get appointments", error });
    }
  });

  app.post("/api/appointments", authenticateToken, async (req: any, res) => {
    try {
      let appointmentData = insertAppointmentSchema.parse(req.body);

      // If user is patient, set patientId to their own ID
      if (req.user.role === 'patient') {
        appointmentData.patientId = req.user.userId;
      }

      const appointment = await storage.createAppointment(appointmentData);
      res.status(201).json(appointment);
    } catch (error) {
      res.status(400).json({ message: "Invalid appointment data", error });
    }
  });

  app.put("/api/appointments/:id", authenticateToken, async (req: any, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;

      const appointment = await storage.updateAppointment(id, updates);
      if (!appointment) {
        return res.status(404).json({ message: "Appointment not found" });
      }

      res.json(appointment);
    } catch (error) {
      res.status(500).json({ message: "Failed to update appointment", error });
    }
  });

  // Available time slots (mock implementation)
  app.get("/api/appointments/available-slots", authenticateToken, async (req, res) => {
    try {
      const { doctorId, date } = req.query;

      // Get existing appointments for the doctor on that date
      const existingAppointments = await storage.getAppointmentsByDate(date as string);
      const doctorAppointments = existingAppointments.filter(apt => apt.doctorId === doctorId);

      // Generate available slots (9 AM to 5 PM, excluding booked ones)
      const allSlots = [
        "09:00", "09:30", "10:00", "10:30", "11:00", "11:30",
        "14:00", "14:30", "15:00", "15:30", "16:00", "16:30"
      ];

      const bookedSlots = doctorAppointments.map(apt => apt.timeSlot);
      const availableSlots = allSlots.filter(slot => !bookedSlots.includes(slot));

      res.json(availableSlots);
    } catch (error) {
      res.status(500).json({ message: "Failed to get available slots", error });
    }
  });

  // Medical Records Routes
  app.get("/api/medical-records", authenticateToken, async (req: any, res) => {
    try {
      let records;

      if (req.user.role === 'patient') {
        records = await storage.getMedicalRecordsByPatient(req.user.userId);
      } else if (req.user.role === 'doctor') {
        const { patientId } = req.query;
        if (patientId) {
          records = await storage.getMedicalRecordsByPatient(patientId as string);
        } else {
          records = await storage.getMedicalRecordsByDoctor(req.user.userId);
        }
      } else if (req.user.role === 'admin' || req.user.role === 'nurse') {
        const { patientId } = req.query;
        if (patientId) {
          records = await storage.getMedicalRecordsByPatient(patientId as string);
        } else {
          // Return all records for admin/nurse would be too much, require patientId
          return res.status(400).json({ message: "patientId parameter required" });
        }
      }

      res.json(records);
    } catch (error) {
      res.status(500).json({ message: "Failed to get medical records", error });
    }
  });

  app.post("/api/medical-records", authenticateToken, authorizeRole(['doctor', 'nurse']), async (req: any, res) => {
    try {
      const recordData = insertMedicalRecordSchema.parse(req.body);

      // Set doctorId to current user if they're a doctor
      if (req.user.role === 'doctor') {
        recordData.doctorId = req.user.userId;
      }

      const record = await storage.createMedicalRecord(recordData);
      res.status(201).json(record);
    } catch (error) {
      res.status(400).json({ message: "Invalid medical record data", error });
    }
  });

  app.put("/api/medical-records/:id", authenticateToken, authorizeRole(['doctor', 'nurse']), async (req: any, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;

      const record = await storage.updateMedicalRecord(id, updates);
      if (!record) {
        return res.status(404).json({ message: "Medical record not found" });
      }

      res.json(record);
    } catch (error) {
      res.status(500).json({ message: "Failed to update medical record", error });
    }
  });

  // Health Metrics Routes
  app.get("/api/health-metrics", authenticateToken, async (req: any, res) => {
    try {
      const { patientId } = req.query;
      let targetPatientId = patientId as string;

      // If user is patient, only allow access to their own metrics
      if (req.user.role === 'patient') {
        targetPatientId = req.user.userId;
      }

      if (!targetPatientId) {
        return res.status(400).json({ message: "patientId parameter required" });
      }

      const metrics = await storage.getHealthMetricsByPatient(targetPatientId);
      res.json(metrics);
    } catch (error) {
      res.status(500).json({ message: "Failed to get health metrics", error });
    }
  });

  app.post("/api/health-metrics", authenticateToken, async (req: any, res) => {
    try {
      let metricsData = insertHealthMetricsSchema.parse(req.body);

      // If user is patient, set patientId to their own ID
      if (req.user.role === 'patient') {
        metricsData.patientId = req.user.userId;
      }

      const metrics = await storage.createHealthMetrics(metricsData);
      res.status(201).json(metrics);
    } catch (error) {
      res.status(400).json({ message: "Invalid health metrics data", error });
    }
  });

  // Contact Messages Routes
  app.post("/api/contact", async (req, res) => {
    try {
      const messageData = insertContactMessageSchema.parse(req.body);
      const message = await storage.createContactMessage(messageData);
      res.status(201).json(message);
    } catch (error) {
      res.status(400).json({ message: "Invalid contact message data", error });
    }
  });

  app.get("/api/contact", authenticateToken, authorizeRole(['admin']), async (req, res) => {
    try {
      const messages = await storage.getAllContactMessages();
      res.json(messages);
    } catch (error) {
      res.status(500).json({ message: "Failed to get contact messages", error });
    }
  });

  // Feedback Routes
  app.post("/api/feedback", authenticateToken, async (req: any, res) => {
    try {
      const feedbackData = insertFeedbackSchema.parse({
        ...req.body,
        userId: req.user.userId
      });

      const feedback = await storage.createFeedback(feedbackData);
      res.status(201).json(feedback);
    } catch (error) {
      res.status(400).json({ message: "Invalid feedback data", error });
    }
  });

  app.get("/api/feedback", authenticateToken, authorizeRole(['admin']), async (req, res) => {
    try {
      const feedback = await storage.getAllFeedback();
      res.json(feedback);
    } catch (error) {
      res.status(500).json({ message: "Failed to get feedback", error });
    }
  });

  // Dashboard Analytics Routes
  app.get("/api/analytics/dashboard-stats", authenticateToken, async (req: any, res) => {
    try {
      const users = await storage.getAllUsers();
      const appointments = await storage.getAllAppointments();
      const today = new Date().toISOString().split('T')[0];
      const todayAppointments = await storage.getAppointmentsByDate(today);

      const stats = {
        totalUsers: users.length,
        totalPatients: users.filter(u => u.role === 'patient').length,
        totalDoctors: users.filter(u => u.role === 'doctor').length,
        totalAppointments: appointments.length,
        todayAppointments: todayAppointments.length,
        activeUsers: users.filter(u => u.isActive).length,
      };

      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to get dashboard stats", error });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type Appointment, 
  type InsertAppointment,
  type MedicalRecord,
  type InsertMedicalRecord,
  type HealthMetrics,
  type InsertHealthMetrics,
  type ContactMessage,
  type InsertContactMessage,
  type Feedback,
  type InsertFeedback
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined>;
  getUsersByRole(role: string): Promise<User[]>;
  getAllUsers(): Promise<User[]>;

  // Appointment operations
  getAppointment(id: string): Promise<Appointment | undefined>;
  createAppointment(appointment: InsertAppointment): Promise<Appointment>;
  updateAppointment(id: string, updates: Partial<InsertAppointment>): Promise<Appointment | undefined>;
  getAppointmentsByPatient(patientId: string): Promise<Appointment[]>;
  getAppointmentsByDoctor(doctorId: string): Promise<Appointment[]>;
  getAppointmentsByDate(date: string): Promise<Appointment[]>;
  getAllAppointments(): Promise<Appointment[]>;

  // Medical Record operations
  getMedicalRecord(id: string): Promise<MedicalRecord | undefined>;
  createMedicalRecord(record: InsertMedicalRecord): Promise<MedicalRecord>;
  updateMedicalRecord(id: string, updates: Partial<InsertMedicalRecord>): Promise<MedicalRecord | undefined>;
  getMedicalRecordsByPatient(patientId: string): Promise<MedicalRecord[]>;
  getMedicalRecordsByDoctor(doctorId: string): Promise<MedicalRecord[]>;

  // Health Metrics operations
  getHealthMetrics(id: string): Promise<HealthMetrics | undefined>;
  createHealthMetrics(metrics: InsertHealthMetrics): Promise<HealthMetrics>;
  getHealthMetricsByPatient(patientId: string): Promise<HealthMetrics[]>;

  // Contact Message operations
  createContactMessage(message: InsertContactMessage): Promise<ContactMessage>;
  getAllContactMessages(): Promise<ContactMessage[]>;
  updateContactMessageStatus(id: string, status: string): Promise<ContactMessage | undefined>;

  // Feedback operations
  createFeedback(feedback: InsertFeedback): Promise<Feedback>;
  getAllFeedback(): Promise<Feedback[]>;
  getFeedbackByUser(userId: string): Promise<Feedback[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private appointments: Map<string, Appointment>;
  private medicalRecords: Map<string, MedicalRecord>;
  private healthMetrics: Map<string, HealthMetrics>;
  private contactMessages: Map<string, ContactMessage>;
  private feedbacks: Map<string, Feedback>;

  constructor() {
    this.users = new Map();
    this.appointments = new Map();
    this.medicalRecords = new Map();
    this.healthMetrics = new Map();
    this.contactMessages = new Map();
    this.feedbacks = new Map();
  }

  // User operations
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const now = new Date();
    const user: User = { 
      ...insertUser, 
      id,
      createdAt: now,
      updatedAt: now
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<InsertUser>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser: User = { 
      ...user, 
      ...updates, 
      updatedAt: new Date() 
    };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  async getUsersByRole(role: string): Promise<User[]> {
    return Array.from(this.users.values()).filter(user => user.role === role);
  }

  async getAllUsers(): Promise<User[]> {
    return Array.from(this.users.values());
  }

  // Appointment operations
  async getAppointment(id: string): Promise<Appointment | undefined> {
    return this.appointments.get(id);
  }

  async createAppointment(insertAppointment: InsertAppointment): Promise<Appointment> {
    const id = randomUUID();
    const now = new Date();
    const appointment: Appointment = { 
      ...insertAppointment, 
      id,
      createdAt: now,
      updatedAt: now
    };
    this.appointments.set(id, appointment);
    return appointment;
  }

  async updateAppointment(id: string, updates: Partial<InsertAppointment>): Promise<Appointment | undefined> {
    const appointment = this.appointments.get(id);
    if (!appointment) return undefined;

    const updatedAppointment: Appointment = { 
      ...appointment, 
      ...updates, 
      updatedAt: new Date() 
    };
    this.appointments.set(id, updatedAppointment);
    return updatedAppointment;
  }

  async getAppointmentsByPatient(patientId: string): Promise<Appointment[]> {
    return Array.from(this.appointments.values()).filter(apt => apt.patientId === patientId);
  }

  async getAppointmentsByDoctor(doctorId: string): Promise<Appointment[]> {
    return Array.from(this.appointments.values()).filter(apt => apt.doctorId === doctorId);
  }

  async getAppointmentsByDate(date: string): Promise<Appointment[]> {
    return Array.from(this.appointments.values()).filter(apt => apt.appointmentDate === date);
  }

  async getAllAppointments(): Promise<Appointment[]> {
    return Array.from(this.appointments.values());
  }

  // Medical Record operations
  async getMedicalRecord(id: string): Promise<MedicalRecord | undefined> {
    return this.medicalRecords.get(id);
  }

  async createMedicalRecord(insertRecord: InsertMedicalRecord): Promise<MedicalRecord> {
    const id = randomUUID();
    const now = new Date();
    const record: MedicalRecord = { 
      ...insertRecord, 
      id,
      createdAt: now,
      updatedAt: now
    };
    this.medicalRecords.set(id, record);
    return record;
  }

  async updateMedicalRecord(id: string, updates: Partial<InsertMedicalRecord>): Promise<MedicalRecord | undefined> {
    const record = this.medicalRecords.get(id);
    if (!record) return undefined;

    const updatedRecord: MedicalRecord = { 
      ...record, 
      ...updates, 
      updatedAt: new Date() 
    };
    this.medicalRecords.set(id, updatedRecord);
    return updatedRecord;
  }

  async getMedicalRecordsByPatient(patientId: string): Promise<MedicalRecord[]> {
    return Array.from(this.medicalRecords.values()).filter(record => record.patientId === patientId);
  }

  async getMedicalRecordsByDoctor(doctorId: string): Promise<MedicalRecord[]> {
    return Array.from(this.medicalRecords.values()).filter(record => record.doctorId === doctorId);
  }

  // Health Metrics operations
  async getHealthMetrics(id: string): Promise<HealthMetrics | undefined> {
    return this.healthMetrics.get(id);
  }

  async createHealthMetrics(insertMetrics: InsertHealthMetrics): Promise<HealthMetrics> {
    const id = randomUUID();
    const metrics: HealthMetrics = { 
      ...insertMetrics, 
      id,
      createdAt: new Date()
    };
    this.healthMetrics.set(id, metrics);
    return metrics;
  }

  async getHealthMetricsByPatient(patientId: string): Promise<HealthMetrics[]> {
    return Array.from(this.healthMetrics.values()).filter(metrics => metrics.patientId === patientId);
  }

  // Contact Message operations
  async createContactMessage(insertMessage: InsertContactMessage): Promise<ContactMessage> {
    const id = randomUUID();
    const message: ContactMessage = { 
      ...insertMessage, 
      id,
      createdAt: new Date()
    };
    this.contactMessages.set(id, message);
    return message;
  }

  async getAllContactMessages(): Promise<ContactMessage[]> {
    return Array.from(this.contactMessages.values());
  }

  async updateContactMessageStatus(id: string, status: string): Promise<ContactMessage | undefined> {
    const message = this.contactMessages.get(id);
    if (!message) return undefined;

    const updatedMessage: ContactMessage = { 
      ...message, 
      status 
    };
    this.contactMessages.set(id, updatedMessage);
    return updatedMessage;
  }

  // Feedback operations
  async createFeedback(insertFeedback: InsertFeedback): Promise<Feedback> {
    const id = randomUUID();
    const feedback: Feedback = { 
      ...insertFeedback, 
      id,
      createdAt: new Date()
    };
    this.feedbacks.set(id, feedback);
    return feedback;
  }

  async getAllFeedback(): Promise<Feedback[]> {
    return Array.from(this.feedbacks.values());
  }

  async getFeedbackByUser(userId: string): Promise<Feedback[]> {
    return Array.from(this.feedbacks.values()).filter(feedback => feedback.userId === userId);
  }
}

export const storage = new MemStorage();

