/* Custom 3D Button Animation */
.container-button {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-template-rows: 1fr 1fr;
  grid-template-areas: "bt-1 bt-2 bt-3" "bt-4 bt-5 bt-6";
  position: relative;
  perspective: 800px;
  padding: 0;
  width: 140px;
  height: 50px;
  transition: all 0.3s ease-in-out;
}

.container-button:active {
  transform: scale(0.95);
}

.hover {
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 200;
}

.bt-1 { grid-area: bt-1; }
.bt-2 { grid-area: bt-2; }
.bt-3 { grid-area: bt-3; }
.bt-4 { grid-area: bt-4; }
.bt-5 { grid-area: bt-5; }
.bt-6 { grid-area: bt-6; }

.bt-1:hover ~ .animated-button {
  transform: rotateX(15deg) rotateY(-15deg) rotateZ(0deg);
  box-shadow: -2px -2px hsl(20, 14.3%, 4.1%, 0.5);
}

.bt-1:hover ~ .animated-button::after {
  animation: shake 0.5s ease-in-out 0.3s;
  text-shadow: -2px -2px hsl(20, 14.3%, 4.1%, 0.5);
}

.bt-3:hover ~ .animated-button {
  transform: rotateX(15deg) rotateY(15deg) rotateZ(0deg);
  box-shadow: 2px -2px hsl(20, 14.3%, 4.1%, 0.5);
}

.bt-3:hover ~ .animated-button::after {
  animation: shake 0.5s ease-in-out 0.3s;
  text-shadow: 2px -2px hsl(20, 14.3%, 4.1%, 0.5);
}

.bt-4:hover ~ .animated-button {
  transform: rotateX(-15deg) rotateY(-15deg) rotateZ(0deg);
  box-shadow: -2px 2px hsl(20, 14.3%, 4.1%, 0.5);
}

.bt-4:hover ~ .animated-button::after {
  animation: shake 0.5s ease-in-out 0.3s;
  text-shadow: -2px 2px hsl(20, 14.3%, 4.1%, 0.5);
}

.bt-6:hover ~ .animated-button {
  transform: rotateX(-15deg) rotateY(15deg) rotateZ(0deg);
  box-shadow: 2px 2px hsl(20, 14.3%, 4.1%, 0.5);
}

.bt-6:hover ~ .animated-button::after {
  animation: shake 0.5s ease-in-out 0.3s;
  text-shadow: 2px 2px hsl(20, 14.3%, 4.1%, 0.5);
}

.hover:hover ~ .animated-button::before {
  background: transparent;
}

.hover:hover ~ .animated-button::after {
  content: "Login";
  top: -150%;
  transform: translate(-50%, 0);
  font-size: 24px;
